import React, { Component } from "react";
import tether from "../tether.png";
import Airdrop from "./Airdrop";

// create class for component
class Main extends Component {
  render() {
    return (
      <div
        id="content"
        style={{
          position: "relative",
          backgroundColor: "#FFFFFF",
          marginTop: "30%",
          borderRadius: "10px",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            marginLeft: "15%",
            marginTop: "15%",
            display: "inline-block",
            width: "70%",
          }}
        >
          <div style={{ fontSize: "12px" }}>
            {" "}
            <b style={{ fontSize: "23px" }}>Stake Tokens</b>
            <br />
            Start by inputing the amount you would like to stake/unstake
          </div>
          <div style={{ marginTop: "10%", width: "100%" }}>
            <form
              onSubmit={(event) => {
                // button can only be hit once
                event.preventDefault();
                let amount;
                amount = this.input.value.toString();
                amount = window.web3.utils.toWei(amount, "Ether");
                this.props.stakeTokens(amount);
              }}
            >
              <div style={{ display: "flex" }}>
                <input
                  ref={(input) => {
                    this.input = input;
                  }}
                  type="text"
                  placeholder="Token Ammount"
                  style={{
                    paddingLeft: "5%",
                    borderRadius: "7px",
                    height: "40px",
                    borderColor: "#E5E5E5",
                    width: "75%",
                  }}
                  required
                />
                <div className="input-group-open">
                  <div className="input-group-text" style={{ color: "black" }}>
                    <b>mUSDT</b>&nbsp;&nbsp;&nbsp;
                    <img alt="tether" src={tether} height="22" />
                  </div>
                </div>
              </div>
              <div
                style={{
                  display: "flex",
                  marginTop: "5%",
                  justifyContent: "space-between",
                }}
              >
                <div>
                  {" "}
                  <button
                    onClick={(event) => {
                      event.preventDefault();
                      this.props.unstakeTokens();
                    }}
                    className="btn btn-block"
                    style={{
                      color: "white",
                      backgroundColor: "#202224",
                      width: "190px",
                      borderRadius: "10px",
                    }}
                  >
                    UNSTAKE
                  </button>
                </div>
                <div>
                  <button
                    type="submit"
                    className="btn btn-block"
                    style={{
                      backgroundColor: "#FECA33",
                      width: "190px",
                      borderRadius: "10px",
                      fontWeight: "bold",
                    }}
                  >
                    STAKE
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
        <div
          style={{
            backgroundColor: "#000000",
            width: "80%",
            margin: "auto",
            borderRadius: "10px",
            opacity: "5%",
            display: "flex",
          }}
        >
          <div style={{ width: "30%" }}>
            <div>hi</div>
            <div>hi</div>
          </div>
          <div style={{ width: "30%" }}>hello</div>
          <div style={{ width: "30%" }}>hello</div>
        </div>
      </div>
    );
  }
}

export default Main;
