import React, { Component } from "react";
import tether from "../tether.png";
import Airdrop from "./Airdrop";

// create class for component
class Main extends Component {
  render() {
    return (
      <div
        id="content"
        style={{
          position: "relative",
          backgroundColor: "#FFFFFF",
          marginTop: "30%",
          borderRadius: "10px",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            marginLeft: "15%",
            marginTop: "15%",
            marginBottom: "15%",
            display: "inline-block",
          }}
        >
          <div style={{ fontSize: "12px" }}>
            {" "}
            <b style={{ fontSize: "23px" }}>Stake Tokens</b>
            <br />
            Start by inputing the amount you would like to stake/unstake
          </div>
          <div style={{ marginTop: "10%" }}>
            <form
              onSubmit={(event) => {
                // button can only be hit once
                event.preventDefault();
                let amount;
                amount = this.input.value.toString();
                amount = window.web3.utils.toWei(amount, "Ether");
                this.props.stakeTokens(amount);
              }}
            >
              <div style={{ display: "flex" }}>
                <input
                  ref={(input) => {
                    this.input = input;
                  }}
                  type="text"
                  placeholder="Token Ammount"
                  style={{
                    paddingLeft: "5%",
                    borderRadius: "7px",
                    height: "40px",
                    borderColor: "#E5E5E5",
                    width: "75%",
                  }}
                  required
                />
                <div className="input-group-open">
                  <div className="input-group-text" style={{ color: "black" }}>
                    <b>mUSDT</b>&nbsp;&nbsp;&nbsp;
                    <img alt="tether" src={tether} height="22" />
                  </div>
                </div>
              </div>
              <div>
                <div></div>{" "}
                <button
                  type="submit"
                  className="btn btn-primary btn-lg btn-block"
                  style={{ width: "60%", marginLeft: "20%" }}
                >
                  STAKE
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}

export default Main;
