import React, { Component } from "react";
import tether from "../tether.png";
import Airdrop from "./Airdrop";

// create class for component
class Main extends Component {
  render() {
    return (
      <div
        id="content"
        style={{
          position: "relative",
          backgroundColor: "#FFFFFF",
          marginTop: "30%",

          borderRadius: "10px",

          justifyContent: "center",
        }}
      >
        <div>
          <div style={{ fontSize: "12px" }}>
            {" "}
            <b style={{ fontSize: "23px" }}>Stake Tokens</b>
            <br />
            Start by inputing the amount you would like to stake/unstake
          </div>
          <div>
            <form
              onSubmit={(event) => {
                // button can only be hit once
                event.preventDefault();
                let amount;
                amount = this.input.value.toString();
                amount = window.web3.utils.toWei(amount, "Ether");
                this.props.stakeTokens(amount);
              }}
              className="mb-3"
            >
              <div style={{ borderSpacing: "0 1em", padding: "5% 0% 0% 0%" }}>
                <div style={{ padding: "0% 5% 0% 5%" }}>
                  <span className="float-right" style={{ marginRight: "8px" }}>
                    <b>Balance:</b>{" "}
                    {window.web3.utils.fromWei(
                      this.props.tetherBalance,
                      "Ether"
                    )}
                    &nbsp;mUSDT
                  </span>
                  <div className="input-group mb-4">
                    <input
                      ref={(input) => {
                        this.input = input;
                      }}
                      type="text"
                      placeholder="0"
                      style={{ marginLeft: "5px" }}
                      required
                    />
                    <div className="input-group-open">
                      <div className="input-group-text">
                        <img alt="tether" src={tether} height="33" />
                        &nbsp;&nbsp;&nbsp;mUSDT
                      </div>
                    </div>
                  </div>
                </div>

                <button
                  type="submit"
                  className="btn btn-primary btn-lg btn-block"
                  style={{ width: "60%", marginLeft: "20%" }}
                >
                  STAKE
                </button>
              </div>
            </form>
          </div>
          <div></div>
        </div>
      </div>
    );
  }
}

export default Main;
