import React, { Component } from "react";

class Airdrop extends Component {
  constructor() {
    super();
    this.state = {
      time: {},
      seconds: 30,
    };
    this.timer = 0;
    this.startTime = this.startTimebind(this);
    this.countDown = this.countDown(this);
  }

  // function to calculate how many hours, minutes, and seconds from total seconds
  secondsToTime(secs) {
    let hours, seconds, minutes;
    hours = Math.floor(secs / (60 * 60));
    let remainder_for_minutes = secs % (60 * 60);
    minutes = Math.floor(remainder_for_minutes / 60);
    let remainder_for_seconds = remainder_for_minutes % 60;
    seconds = Math.ceil(remainder_for_seconds / 60);

    let obj = {
      h: hours,
      m: minutes,
      s: seconds,
    };

    return obj;
  }

  componentDidMount() {
    let timeLeft = this.secondsToTime(this.state.seconds);
    this.setState = { time: timeLeft };
  }

  render() {
    return (
      <div style={{ color: "black" }}>
        {this.state.time.m}:{this.state.time.s}
      </div>
    );
  }
}

export default Airdrop;
